import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

function Carrito() {
  const { cart, removeFromCart, updateQuantity, total, clearCart } = useCart();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handlePagar = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:8000/api/productos/iniciar_pago/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ total: total })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Error al iniciar el pago');
      }

      const data = await response.json();

      // Redirigir al usuario a la URL de Transbank para completar el pago
      window.location.href = data.url;
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ textAlign: 'center' }}>Carrito de Compras</h1>

      {cart.length === 0 ? (
        <p>Tu carrito está vacío.</p>
      ) : (
        <>
          {cart.map(item => (
            <div key={item.id} style={{ borderBottom: '1px solid #ccc', marginBottom: '10px', paddingBottom: '10px' }}>
              <h3>{item.nombre}</h3>
              <p>{item.descripcion}</p>
              <input
                type="number"
                value={item.cantidad}
                onChange={(e) => updateQuantity(item.id, e.target.value)}
                min="1"
              />
              <p>Precio unitario: ${item.precio}</p>
              <p>Subtotal: ${item.precio * item.cantidad}</p>
              <button onClick={() => removeFromCart(item.id)}>Eliminar</button>
            </div>
          ))}
          <h2>Total: ${total.toFixed(2)}</h2>
          <button onClick={clearCart} style={{ backgroundColor: 'red', color: 'white' }}>
            Vaciar Carrito
          </button>

          <button
            onClick={handlePagar}
            disabled={loading}
            style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}
          >
            {loading ? 'Procesando pago...' : 'Pagar con Transbank'}
          </button>

          {error && <p style={{ color: 'red' }}>Error: {error}</p>}
        </>
      )}
    </div>
  );
}

export default Carrito;
