import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

function Carrito() {
  const { cart, total } = useCart();
  const [loading, setLoading] = useState(false);

  const handlePagar = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:8000/api/productos/iniciar_pago/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ total: total.toFixed(2) }),
      });
      const data = await response.json();
      if (data.url && data.token) {
        // Redirigir a la url de Webpay con el token
        window.location.href = `${data.url}?token_ws=${data.token}`;
      } else {
        alert('Error: No se recibió URL ni token');
      }
    } catch (error) {
      alert('Error al iniciar pago');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      {/* Código para mostrar productos */}
      <h2>Total: ${total.toFixed(2)}</h2>
      <button onClick={handlePagar} disabled={loading}>
        {loading ? 'Redirigiendo...' : 'Pagar con Transbank'}
      </button>
    </div>
  );
}

export default Carrito;
